import React, { Component } from 'react'

class TextInput extends Component {
    constructor(props) {
        super(props)
    }

    state = {
        input: ''
    }

    onChange = (e) => {
        this.setState({title: e.target.value})
    }

    onSubmit = () => {
        //Call backend
        console.log(this.state.input)
    }

    render() {
        return (
            <div>
            <form style={{ flex: 3, alignItems: 'stretch', flexDirection: 'row'}} onSubmit={this.onSubmit}>
                <div style={{innerWidth: '100%'}}>
                <input
                    type="text"
                    name="title"
                    style={{ flex: 1, padding: '5px'}}
                    placeholder="Add Todo ..."
                    value={this.state.title}
                    onChange={this.onChange}
                /></div>
                <input
                    type="submit"
                    value="Submit"
                    className="btn"
                />
            </form>
            </div>
        )
    }
}

export default TextInput
