import React from 'react';
import './App.css';

import Header from './component/layout/Header'
import TextInput from './component/pages/TextInput'
import Progress from './component/pages/DownloadProgress'

class App extends React.Component {
  state = {
    submitted: false
  }

  constructor(props) {
    super(props);
  }

  onSubmit = (input) => {
    console.log(input)
    this.setState({submitted: true})
    //CALL BACKEND
    var request = new XMLHttpRequest()

    request.open('GET', 'https://localhost:8080/tts/json', true)

    request.onload = function() {
      request = {
        text: input
      }
    }

    request.send()

    var data = JSON.parse(this.response)

    data.forEach(response => {
      // Log each movie's title
      console.log(response.filename)
    })
  }

  render() {
    return (
      <div className="App">
        <Header/>
        <TextInput onSubmit={this.onSubmit}/>
        {this.state.submitted ? <Progress/> : null}
      </div>
    );
  }
}

export default App;
